mapper.select:
SELECT 

FROM bus_customer a

WHERE a.id = #{id}

SELECT 

FROM bus_customer a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.area_id = #{area.id}


		AND a.company_name = #{companyName}


		AND a.contact_person = #{contactPerson}


		AND a.cellphone = #{cellphone}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bus_customer a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bus_customer SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
