mapper.select:
SELECT 

FROM bus_factory a

WHERE a.id = #{id}

SELECT 

FROM bus_factory a



		AND a.name = #{name}



			and a.del_flag = #{delFlag}


			and a.del_flag = #{DEL_FLAG_NORMAL}





		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bus_factory a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bus_factory SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
