mapper.select:
SELECT 

FROM bus_material a

WHERE a.id = #{id}

SELECT 

FROM bus_material a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.type = #{type}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.quantity = #{quantity}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bus_material a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bus_material SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
