mapper.select:
SELECT 

FROM bus_product a

WHERE a.id = #{id}

SELECT
FROM bus_product a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM bus_product a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bus_product SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
