mapper.select:
SELECT 

FROM bus_purchase_order a

WHERE a.id = #{id}

SELECT 

FROM bus_purchase_order a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.apply_by = #{applyBy}


		AND a.apply_date = #{applyDate}


		AND a.audit_by = #{auditBy}


		AND a.audit_date = #{auditDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bus_purchase_order a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bus_purchase_order SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
