mapper.select:
SELECT 

FROM bus_sale_order a

WHERE a.id = #{id}

SELECT 

FROM bus_sale_order a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.customer_id = #{customer.id}


		AND a.status = #{status}


		AND a.order_date = #{orderDate}


		AND a.delivery_date = #{deliveryDate}


		AND a.delivery_cycle = #{deliveryCycle}


		AND a.has_after_sale = #{hasAfterSale}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bus_sale_order a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bus_sale_order SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
