mapper.select:
SELECT 

FROM bus_sale_order_detail a

WHERE a.id = #{id}

SELECT 

FROM bus_sale_order_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.sale_order_id = #{saleOrder.id}


		AND a.product_id = #{producId}


		AND a.quantity = #{quantity}


		AND a.price = #{price}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM bus_sale_order_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE bus_sale_order_detail SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE sale_order_id = #{saleOrder.id}
